import './location.css';

export default function Location(props) {
  return (
    <div className="location">
      <img className="location--pic" src={props.details.imageUrl} alt={props.details.name} />
      <div className="location--info">
        <h2 className="location--name">{props.details.name}</h2>
        <p className="location--description">{props.details.description}</p>
      </div>
    </div>
  )
}
