import './location.css';
import mapPointer from '../../images/locationMap.svg';

export default function Location(props) {
  return (
    <div className="location">
      <img className="location--pic" src={props.details.imageUrl} alt={props.details.title} />
      <div className="location--info">
        <div className="location--google">
          <img src={mapPointer} alt="map pointer" />
          <span>{props.details.location}</span>
          <a href={props.details.googleMapsUrl} target="_blank" rel="noopener noreferrer">
            View on Google Maps
          </a>
        </div>
        <h2 className="location--name">{props.details.title}</h2>
        <p className="location--description">{props.details.description}</p>
      </div>
    </div>
  )
}
